  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPatientSchema, insertAppointmentSchema, insertPatientFileSchema, insertToothRecordSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Dashboard stats
  app.get("/api/dashboard/stats", async (req, res) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Patients routes
  app.get("/api/patients", async (req, res) => {
    try {
      const { search, pageNumber, pageSize } = req.query;
      const params = {
        search: search as string,
        pageNumber: pageNumber ? parseInt(pageNumber as string) : undefined,
        pageSize: pageSize ? parseInt(pageSize as string) : undefined,
      };
      const result = await storage.getPatients(params);
      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch patients" });
    }
  });

  app.get("/api/patients/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const patient = await storage.getPatient(id);
      if (!patient) {
        return res.status(404).json({ message: "Patient not found" });
      }
      res.json(patient);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch patient" });
    }
  });

  app.post("/api/patients", async (req, res) => {
    try {
      const validatedData = insertPatientSchema.parse(req.body);
      const patient = await storage.createPatient(validatedData);
      res.status(201).json(patient);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create patient" });
    }
  });

  app.put("/api/patients/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertPatientSchema.partial().parse(req.body);
      const patient = await storage.updatePatient(id, validatedData);
      if (!patient) {
        return res.status(404).json({ message: "Patient not found" });
      }
      res.json(patient);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update patient" });
    }
  });

  app.delete("/api/patients/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.deletePatient(id);
      if (!success) {
        return res.status(404).json({ message: "Patient not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete patient" });
    }
  });

  // Appointments routes
  app.get("/api/appointments", async (req, res) => {
    try {
      const { patientId, date, pageNumber, pageSize } = req.query;
      const params = {
        patientId: patientId ? parseInt(patientId as string) : undefined,
        date: date as string,
        pageNumber: pageNumber ? parseInt(pageNumber as string) : undefined,
        pageSize: pageSize ? parseInt(pageSize as string) : undefined,
      };
      const result = await storage.getAppointments(params);
      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch appointments" });
    }
  });

  app.get("/api/appointments/today", async (req, res) => {
    try {
      const appointments = await storage.getTodayAppointments();
      res.json(appointments);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch today's appointments" });
    }
  });

  app.post("/api/appointments", async (req, res) => {
    try {
      const validatedData = insertAppointmentSchema.parse(req.body);
      const appointment = await storage.createAppointment(validatedData);
      res.status(201).json(appointment);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create appointment" });
    }
  });

  // Files routes
  app.get("/api/patients/:patientId/files", async (req, res) => {
    try {
      const patientId = parseInt(req.params.patientId);
      const files = await storage.getPatientFiles(patientId);
      res.json(files);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch patient files" });
    }
  });

  app.post("/api/files", async (req, res) => {
    try {
      const validatedData = insertPatientFileSchema.parse(req.body);
      const file = await storage.createPatientFile(validatedData);
      res.status(201).json(file);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create file record" });
    }
  });

  app.delete("/api/files/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.deletePatientFile(id);
      if (!success) {
        return res.status(404).json({ message: "File not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete file" });
    }
  });

  // Medical conditions
  app.get("/api/medical-conditions", async (req, res) => {
    try {
      const conditions = await storage.getMedicalConditions();
      res.json(conditions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch medical conditions" });
    }
  });

  // Tooth Records (Odontogram) routes
  app.get("/api/patients/:patientId/teeth", async (req, res) => {
    try {
      const patientId = parseInt(req.params.patientId);
      const toothRecords = await storage.getPatientToothRecords(patientId);
      res.json(toothRecords);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch tooth records" });
    }
  });

  app.post("/api/teeth", async (req, res) => {
    try {
      const validatedData = insertToothRecordSchema.parse(req.body);
      const toothRecord = await storage.createToothRecord(validatedData);
      res.status(201).json(toothRecord);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create tooth record" });
    }
  });

  app.put("/api/teeth/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertToothRecordSchema.partial().parse(req.body);
      const toothRecord = await storage.updateToothRecord(id, validatedData);
      if (!toothRecord) {
        return res.status(404).json({ message: "Tooth record not found" });
      }
      res.json(toothRecord);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update tooth record" });
    }
  });

  app.delete("/api/teeth/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.deleteToothRecord(id);
      if (!success) {
        return res.status(404).json({ message: "Tooth record not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete tooth record" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type Patient, 
  type InsertPatient,
  type Appointment,
  type InsertAppointment,
  type PatientFile,
  type InsertPatientFile,
  type ToothRecord,
  type InsertToothRecord,
  type MedicalCondition,
  type DashboardStats,
  type PaginatedResponse
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Patients
  getPatients(params: { search?: string; pageNumber?: number; pageSize?: number }): Promise<PaginatedResponse<Patient>>;
  getPatient(id: number): Promise<Patient | undefined>;
  createPatient(patient: InsertPatient): Promise<Patient>;
  updatePatient(id: number, patient: Partial<InsertPatient>): Promise<Patient | undefined>;
  deletePatient(id: number): Promise<boolean>;

  // Appointments
  getAppointments(params: { patientId?: number; date?: string; pageNumber?: number; pageSize?: number }): Promise<PaginatedResponse<Appointment>>;
  getAppointment(id: number): Promise<Appointment | undefined>;
  createAppointment(appointment: InsertAppointment): Promise<Appointment>;
  updateAppointment(id: number, appointment: Partial<InsertAppointment>): Promise<Appointment | undefined>;
  deleteAppointment(id: number): Promise<boolean>;
  getTodayAppointments(): Promise<Appointment[]>;

  // Files
  getPatientFiles(patientId: number): Promise<PatientFile[]>;
  createPatientFile(file: InsertPatientFile): Promise<PatientFile>;
  deletePatientFile(id: number): Promise<boolean>;

  // Medical Conditions
  getMedicalConditions(): Promise<MedicalCondition[]>;

  // Tooth Records (Odontogram)
  getPatientToothRecords(patientId: number): Promise<ToothRecord[]>;
  createToothRecord(record: InsertToothRecord): Promise<ToothRecord>;
  updateToothRecord(id: number, record: Partial<InsertToothRecord>): Promise<ToothRecord | undefined>;
  deleteToothRecord(id: number): Promise<boolean>;

  // Dashboard
  getDashboardStats(): Promise<DashboardStats>;
}

export class MemStorage implements IStorage {
  private patients: Map<number, Patient>;
  private appointments: Map<number, Appointment>;
  private patientFiles: Map<number, PatientFile>;
  private medicalConditions: Map<number, MedicalCondition>;
  private toothRecords: Map<number, ToothRecord>;
  private idCounters: { patients: number; appointments: number; files: number; conditions: number; teeth: number };

  constructor() {
    this.patients = new Map();
    this.appointments = new Map();
    this.patientFiles = new Map();
    this.medicalConditions = new Map();
    this.toothRecords = new Map();
    this.idCounters = { patients: 1, appointments: 1, files: 1, conditions: 1, teeth: 1 };

    this.seedData();
  }

  private seedData() {
    // Seed patients
    const samplePatients: Patient[] = [
      {
        id: 1,
        firstName: "John",
        lastName: "Doe",
        phone: "+1 234 567 8900",
        email: "john.doe@email.com",
        dateOfBirth: "1985-01-15",
        address: "123 Main St, City, State 12345",
        gender: "Male",
        jmbg: "1234567890123",
        statusId: 1,
        createdAt: new Date("2024-12-01"),
        updatedAt: new Date("2024-12-15")
      },
      {
        id: 2,
        firstName: "Jane",
        lastName: "Smith",
        phone: "+1 234 567 8901",
        email: "jane.smith@email.com",
        dateOfBirth: "1992-03-22",
        address: "456 Oak Ave, City, State 12345",
        gender: "Female",
        jmbg: "1234567890124",
        statusId: 1,
        createdAt: new Date("2024-12-02"),
        updatedAt: new Date("2024-12-14")
      },
      {
        id: 3,
        firstName: "Robert",
        lastName: "Johnson",
        phone: "+1 234 567 8902",
        email: "robert.j@email.com",
        dateOfBirth: "1978-11-08",
        address: "789 Pine Rd, City, State 12345",
        gender: "Male",
        jmbg: "1234567890125",
        statusId: 1,
        createdAt: new Date("2024-12-03"),
        updatedAt: new Date("2024-12-13")
      }
    ];

    samplePatients.forEach(patient => {
      this.patients.set(patient.id, patient);
    });
    this.idCounters.patients = 4;

    // Seed appointments
    const sampleAppointments: Appointment[] = [
      {
        id: 1,
        patientId: 1,
        appointmentDate: new Date("2024-12-20T10:00:00"),
        duration: 60,
        description: "Routine Checkup",
        status: "scheduled",
        createdAt: new Date("2024-12-15"),
        updatedAt: new Date("2024-12-15")
      },
      {
        id: 2,
        patientId: 2,
        appointmentDate: new Date("2024-12-20T11:30:00"),
        duration: 45,
        description: "Cleaning",
        status: "completed",
        createdAt: new Date("2024-12-15"),
        updatedAt: new Date("2024-12-15")
      }
    ];

    sampleAppointments.forEach(appointment => {
      this.appointments.set(appointment.id, appointment);
    });
    this.idCounters.appointments = 3;
  }

  async getPatients(params: { search?: string; pageNumber?: number; pageSize?: number }): Promise<PaginatedResponse<Patient>> {
    const { search = "", pageNumber = 1, pageSize = 10 } = params;
    let allPatients = Array.from(this.patients.values());

    if (search) {
      allPatients = allPatients.filter(patient =>
        patient.firstName.toLowerCase().includes(search.toLowerCase()) ||
        patient.lastName.toLowerCase().includes(search.toLowerCase()) ||
        patient.phone.includes(search) ||
        patient.email?.toLowerCase().includes(search.toLowerCase())
      );
    }

    const totalCount = allPatients.length;
    const totalPages = Math.ceil(totalCount / pageSize);
    const startIndex = (pageNumber - 1) * pageSize;
    const items = allPatients.slice(startIndex, startIndex + pageSize);

    return {
      items,
      totalCount,
      pageNumber,
      pageSize,
      totalPages
    };
  }

  async getPatient(id: number): Promise<Patient | undefined> {
    return this.patients.get(id);
  }

  async createPatient(patient: InsertPatient): Promise<Patient> {
    const id = this.idCounters.patients++;
    const newPatient: Patient = {
      ...patient,
      id,
      address: patient.address || null,
      email: patient.email || null,
      statusId: 1,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.patients.set(id, newPatient);
    return newPatient;
  }

  async updatePatient(id: number, patient: Partial<InsertPatient>): Promise<Patient | undefined> {
    const existing = this.patients.get(id);
    if (!existing) return undefined;

    const updated: Patient = {
      ...existing,
      ...patient,
      updatedAt: new Date()
    };
    this.patients.set(id, updated);
    return updated;
  }

  async deletePatient(id: number): Promise<boolean> {
    return this.patients.delete(id);
  }

  async getAppointments(params: { patientId?: number; date?: string; pageNumber?: number; pageSize?: number }): Promise<PaginatedResponse<Appointment>> {
    const { patientId, date, pageNumber = 1, pageSize = 10 } = params;
    let allAppointments = Array.from(this.appointments.values());

    if (patientId) {
      allAppointments = allAppointments.filter(appointment => appointment.patientId === patientId);
    }

    if (date) {
      const targetDate = new Date(date);
      allAppointments = allAppointments.filter(appointment => {
        const appointmentDate = new Date(appointment.appointmentDate);
        return appointmentDate.toDateString() === targetDate.toDateString();
      });
    }

    const totalCount = allAppointments.length;
    const totalPages = Math.ceil(totalCount / pageSize);
    const startIndex = (pageNumber - 1) * pageSize;
    const items = allAppointments.slice(startIndex, startIndex + pageSize);

    return {
      items,
      totalCount,
      pageNumber,
      pageSize,
      totalPages
    };
  }

  async getAppointment(id: number): Promise<Appointment | undefined> {
    return this.appointments.get(id);
  }

  async createAppointment(appointment: InsertAppointment): Promise<Appointment> {
    const id = this.idCounters.appointments++;
    const newAppointment: Appointment = {
      ...appointment,
      id,
      duration: appointment.duration || 30,
      description: appointment.description || null,
      appointmentDate: new Date(appointment.appointmentDate),
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.appointments.set(id, newAppointment);
    return newAppointment;
  }

  async updateAppointment(id: number, appointment: Partial<InsertAppointment>): Promise<Appointment | undefined> {
    const existing = this.appointments.get(id);
    if (!existing) return undefined;

    const updated: Appointment = {
      ...existing,
      ...appointment,
      appointmentDate: appointment.appointmentDate ? new Date(appointment.appointmentDate) : existing.appointmentDate,
      updatedAt: new Date()
    };
    this.appointments.set(id, updated);
    return updated;
  }

  async deleteAppointment(id: number): Promise<boolean> {
    return this.appointments.delete(id);
  }

  async getTodayAppointments(): Promise<Appointment[]> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);

    return Array.from(this.appointments.values()).filter(appointment => {
      const appointmentDate = new Date(appointment.appointmentDate);
      return appointmentDate >= today && appointmentDate < tomorrow;
    });
  }

  async getPatientFiles(patientId: number): Promise<PatientFile[]> {
    return Array.from(this.patientFiles.values()).filter(file => file.patientId === patientId);
  }

  async createPatientFile(file: InsertPatientFile): Promise<PatientFile> {
    const id = this.idCounters.files++;
    const newFile: PatientFile = {
      ...file,
      id,
      description: file.description || null,
      thumbnailPath: file.thumbnailPath || null,
      uploadedAt: new Date()
    };
    this.patientFiles.set(id, newFile);
    return newFile;
  }

  async deletePatientFile(id: number): Promise<boolean> {
    return this.patientFiles.delete(id);
  }

  async getMedicalConditions(): Promise<MedicalCondition[]> {
    return Array.from(this.medicalConditions.values());
  }

  // Tooth Records (Odontogram) methods
  async getPatientToothRecords(patientId: number): Promise<ToothRecord[]> {
    return Array.from(this.toothRecords.values()).filter(record => record.patientId === patientId);
  }

  async createToothRecord(record: InsertToothRecord): Promise<ToothRecord> {
    const id = this.idCounters.teeth++;
    const newRecord: ToothRecord = {
      ...record,
      id,
      surfaces: record.surfaces || null,
      treatment: record.treatment || null,
      treatmentDate: record.treatmentDate || null,
      notes: record.notes || null,
      appointmentId: record.appointmentId || null,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.toothRecords.set(id, newRecord);
    return newRecord;
  }

  async updateToothRecord(id: number, record: Partial<InsertToothRecord>): Promise<ToothRecord | undefined> {
    const existingRecord = this.toothRecords.get(id);
    if (!existingRecord) return undefined;

    const updatedRecord: ToothRecord = {
      ...existingRecord,
      ...record,
      id,
      updatedAt: new Date()
    };
    this.toothRecords.set(id, updatedRecord);
    return updatedRecord;
  }

  async deleteToothRecord(id: number): Promise<boolean> {
    return this.toothRecords.delete(id);
  }

  async getDashboardStats(): Promise<DashboardStats> {
    const totalPatients = this.patients.size;
    const todayAppointments = (await this.getTodayAppointments()).length;
    const monthlyRevenue = 32849; // Mock value

    return {
      totalPatients,
      todayAppointments,
      monthlyRevenue
    };
  }
}

export const storage = new MemStorage();

